from tkinter import Canvas


class GraphicItem:
    itemType: str
    coords: list
    config: dict

    def __init__(self, cnv: Canvas):
        self.cnv = cnv
        self.uid = None

    def update(self):
        if self.uid is None:
            self.uid = self.cnv._create(
                itemType=self.itemType,
                args=self.coords,
                kw=self.config
            )
        else:
            self.cnv.coords(self.uid, *self.coords)
